use std::str::FromStr;

use ethers::types::{Address, Bytes, Transaction, H256, U256, U64};

use crate::common::RawTransaction;

/// Return the generated Ecotone upgrade RLP-encoded transactions
/// as defined in the [specs].
///
/// [specs]: https://specs.optimism.io/protocol/derivation.html#ecotone
pub fn get_ecotone_upgrade_transactions() -> Vec<RawTransaction> {
    let mut upgrade_transactions = Vec::with_capacity(6);

    let deploy_l1_block = Transaction {
        transaction_type: Some(U64::from(0x7E)),
        source_hash: H256::from_str(
            "0x877a6077205782ea15a6dc8699fa5ebcec5e0f4389f09cb8eda09488231346f8",
        )
        .unwrap(),
        from: Address::from_str("0x4210000000000000000000000000000000000000").unwrap(),
        to: None,
        mint: None,
        value: U256::from(0),
        gas: U256::from(375_000),
        is_system_tx: false,
        input: Bytes::from_str("").unwrap(),
        ..Default::default()
    };
    upgrade_transactions.push(RawTransaction(deploy_l1_block.rlp().to_vec()));

    let deploy_gas_price_oracle = Transaction {
        transaction_type: Some(U64::from(0x7E)),
        source_hash: H256::from_str(
            "0xa312b4510adf943510f05fcc8f15f86995a5066bd83ce11384688ae20e6ecf42",
        )
        .unwrap(),
        from: Address::from_str("0x4210000000000000000000000000000000000001").unwrap(),
        to: None,
        mint: None,
        value: U256::from(0),
        gas: U256::from(1_000_000),
        is_system_tx: false,
        input: Bytes::from_str("").unwrap(),
        ..Default::default()
    };
    upgrade_transactions.push(RawTransaction(deploy_gas_price_oracle.rlp().to_vec()));

    let update_l1_block_proxy = Transaction {
        transaction_type: Some(U64::from(0x7E)),
        source_hash: H256::from_str(
            "0x18acb38c5ff1c238a7460ebc1b421fa49ec4874bdf1e0a530d234104e5e67dbc",
        )
        .unwrap(),
        from: Address::from_str("0x0000000000000000000000000000000000000000").unwrap(),
        to: Some(Address::from_str("0x4200000000000000000000000000000000000015").unwrap()),
        mint: None,
        value: U256::from(0),
        gas: U256::from(50_000),
        is_system_tx: false,
        input: Bytes::from_str(
            "0x3659cfe600000000000000000000000007dbe8500fc591d1852b76fee44d5a05e13097ff",
        )
        .unwrap(),
        ..Default::default()
    };
    upgrade_transactions.push(RawTransaction(update_l1_block_proxy.rlp().to_vec()));

    let update_gas_price_oracle_proxy = Transaction {
        transaction_type: Some(U64::from(0x7E)),
        source_hash: H256::from_str(
            "0xee4f9385eceef498af0be7ec5862229f426dec41c8d42397c7257a5117d9230a",
        )
        .unwrap(),
        from: Address::from_str("0x0000000000000000000000000000000000000000").unwrap(),
        to: Some(Address::from_str("0x420000000000000000000000000000000000000F").unwrap()),
        mint: None,
        value: U256::from(0),
        gas: U256::from(50_000),
        is_system_tx: false,
        input: Bytes::from_str(
            "0x3659cfe6000000000000000000000000b528d11cc114e026f138fe568744c6d45ce6da7a",
        )
        .unwrap(),
        ..Default::default()
    };
    upgrade_transactions.push(RawTransaction(update_gas_price_oracle_proxy.rlp().to_vec()));

    let enable_ecotone = Transaction {
        transaction_type: Some(U64::from(0x7E)),
        source_hash: H256::from_str(
            "0x0c1cb38e99dbc9cbfab3bb80863380b0905290b37eb3d6ab18dc01c1f3e75f93",
        )
        .unwrap(),
        from: Address::from_str("0xDeaDDEaDDeAdDeAdDEAdDEaddeAddEAdDEAd0001").unwrap(),
        to: Some(Address::from_str("0x420000000000000000000000000000000000000F").unwrap()),
        mint: None,
        value: U256::from(0),
        gas: U256::from(80_000),
        is_system_tx: false,
        input: Bytes::from_str("0x22b90ab3").unwrap(),
        ..Default::default()
    };
    upgrade_transactions.push(RawTransaction(enable_ecotone.rlp().to_vec()));

    let deploy_eip4788 = Transaction {
        transaction_type: Some(U64::from(0x7E)),
        source_hash: H256::from_str(
            "0x69b763c48478b9dc2f65ada09b3d92133ec592ea715ec65ad6e7f3dc519dc00c",
        )
        .unwrap(),
        from: Address::from_str("0x0B799C86a49DEeb90402691F1041aa3AF2d3C875").unwrap(),
        to: None,
        mint: None,
        value: U256::from(0),
        gas: U256::from(0x3d090),
        is_system_tx: false,
        input: Bytes::from_str("0x60618060095f395ff33373fffffffffffffffffffffffffffffffffffffffe14604d57602036146024575f5ffd5b5f35801560495762001fff810690815414603c575f5ffd5b62001fff01545f5260205ff35b5f5ffd5b62001fff42064281555f359062001fff015500").unwrap(),
        ..Default::default()
    };
    upgrade_transactions.push(RawTransaction(deploy_eip4788.rlp().to_vec()));

    upgrade_transactions
}
